sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	//gs.addInfoMessage('Checkin Vendor Instance from Maintenance');
	var msg = 'Vendor Instance Maintenance Checkin - ' + current.number.toString() + '\n';
	var utils = new VendorInstanceManagementUtils();
	msg += 'VendorInstanceManagementUtils instantiated as utils' + '\n';
	var gdtCheckin = new GlideDateTime();
	msg += 'gdtCheckin created:  ' + gdtCheckin.getDisplayValue() + '\n';
	var grVIC = utils.findVendorInstanceCheckoutFromTask(current.vendor_instance.toString(), current.sys_id.toString());
	msg += 'ran grVIC = utils.findVendorInstanceCheckoutFromTask(' + current.vendor_instance.toString() + ', ' + current.sys_id.toString() + ')' + '\n';
	if (grVIC.next()) {
		msg += 'grVIC found:  ' + grVIC.number.toString() + '\n';
		var grVICUpdated = utils.updateCheckinDate(grVIC.sys_id.toString(), gdtCheckin);
		msg += 'grVICUpdated.checkin_date = ' + grVICUpdated.checkin_date.getDisplayValue() + '\n';
	}  // if (grVIC.next())
	if (gs.getProperty('glide.installation.production')) {
		var log = new global.GSLog('x_275826_vim.com.vim.log','Checkin Vendor Instance from Maintenance');
		log.logDebug(msg);
	}  // if (gs.getProperty('glide.installation.production'))
})(current, previous);
