sys_ui_action.script:
doCheckinVendorInstance(current);
function doCheckinVendorInstance(grVIC) {
	var msg = '[UI Action] Checkin Instance - ' + current.number.toString() + '\n';
	var utils = new VendorInstanceManagementUtils();
	msg += 'VendorInstanceManagementUtils instantiated as utils' + '\n';
	var boolSuccess = utils.checkinInstance(grVIC.vendor_instance.sys_id.toString()  /* sys_id of vendor_instance */);
	msg += 'utils.checkinInstance(' + current.vendor_instance.toString() + ') returned ' + boolSuccess.toString() + '\n';
	if (boolSuccess) {
		utils.updateCheckinDate(grVIC.sys_id.toString(), new GlideDateTime());
		msg += 'utils.updateCheckinDate(' + grVIC.sys_id.toString() + ') returned ' + boolSuccess.toString() + '\n';
	}  // if (boolSuccess)
	if (gs.getProperty('glide.installation.production')) {
		var log = new global.GSLog('x_275826_vim.com.vim.log','Checkin Instance');
		log.logDebug(msg);
	}  // if (gs.getProperty('glide.installation.production'))
	action.setRedirectURL('x_275826_vim_vendor_instance.do?sys_id=' + grVIC.vendor_instance.sys_id.toString());
}
