sysauto_script.script:
/******************************************************************
*******************************************************************
*  Sets Vendor Instances as Checked in if a Vendor Instance       *
*  Checkout Checkin date is within the threshold set              *
*******************************************************************
******************************************************************/
/**************************************
*****      Activate Debugging     *****
**************************************/
var debug = gs.getProperty('glide.installation.production') == true ? false : true;
// debug = true;    //  manual debug override
/**************************************
**************************************/


var logMsg = 'Checkin Vendor Instance' + '\n';

var grVIC = new GlideRecordSecure('x_275826_vim_vendor_instance_checkout');
grVIC.addEncodedQuery("vendor_instance.checked_out=true^checkin_dateISNOTEMPTY^checkin_dateRELATIVEGE@minute@ago@1^checkin_dateRELATIVELE@minute@ago@" + gs.getProperty('x_275826_vim.vendor_instance_checkin_threshold').toString());
grVIC.query();
logMsg += 'grVIC.getEncodedQuery() = ' + grVIC.getEncodedQuery() + '\n' + 'grVIC.getRowCount() = ' + grVIC.getRowCount() + '\n';
while (grVIC.next()) {
	var utils = new VendorInstanceManagementUtils();
	var boolSuccess = utils.checkinInstance(grVIC.vendor_instance.toString());
	logMsg += '\t' + '(utils.checkinInstance(' + grVIC.vendor_instance.toString() + ')) boolSuccess = ' + boolSuccess.toString() + '\n';
}  // while (grVIC.next())
if (debug) {
	gs.info(logMsg);
}  // if (this.debug)
