sysauto_script.script:
/******************************************************************
*******************************************************************
*  Sets Vendor Instances as Checked out if a Vendor Instance      *
*  Checkout Checkout date is within the threshold set             *
*******************************************************************
******************************************************************/

/**************************************
*****      Activate Debugging     *****
**************************************/
var debug = gs.getProperty('glide.installation.production') == true ? false : true;
debug = true;    //  manual debug override
/**************************************
**************************************/


var logMsg = 'Checkout Vendor Instance' + '\n';
var grVIC = new GlideRecordSecure('x_275826_vim_vendor_instance_checkout');
grVIC.addEncodedQuery("vendor_instance.checked_out=false^checkout_dateRELATIVELE@minute@ahead@" + gs.getProperty('x_275826_vim.vendor_instance_checkout_threshold').toString());
grVIC.query();
logMsg += 'grVIC.getEncodedQuery() = ' + grVIC.getEncodedQuery() + '\n' + 'grVIC.getRowCount() = ' + grVIC.getRowCount() + '\n';
while (grVIC.next()) {
	var utils = new VendorInstanceManagementUtils();
	var boolSuccess = utils.checkoutInstance(grVIC.vendor_instance.toString(), grVIC.sys_id.toString());
	logMsg += '\t' + '(utils.checkoutInstance(' + grVIC.vendor_instance.toString() + ', ' + grVIC.sys_id.toString() + ')) boolSuccess = ' + boolSuccess.toString() + '\n';
}  // while (grVIC.next())
if (debug) {
	gs.info(logMsg);
}  // if (this.debug)
